mapper.select:
select * from article_info
where article_state=0
order by latest_time desc;

mapper.update:
update article_info
set article_state=1
where article_id=#{articleId}

update article_info
set
latest_time=#{latestTime},
article_context=#{articleContext}
where article_id=#{articleId}

update article_info
set
praise_num=#{praiseNum}
where article_id=#{articleId}
